import React from 'react';

interface BackButtonProps {
  onClick: () => void;
  text?: string;
  icon?: string;
  style?: React.CSSProperties;
}

export const BackButton: React.FC<BackButtonProps> = ({ 
  onClick, 
  text = "Назад", 
  icon = "←",
  style
}) => {
  // Базовый стиль кнопки - редактируйте здесь для изменения внешнего вида
  const defaultStyle: React.CSSProperties = {
    position: 'absolute',
    left: 8,
    top: 8,
    background: 'none',
    border: 'none',
    cursor: 'pointer',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    padding: 4,
    borderRadius: 8,
    ...style // позволяет переопределить стиль при необходимости
  };

  const iconStyle: React.CSSProperties = {
    fontSize: 28,
    lineHeight: 1,
    color: '#1976d2'
  };

  const textStyle: React.CSSProperties = {
    fontSize: 13,
    color: '#1976d2',
    marginTop: 2
  };

  return (
    <button
      style={defaultStyle}
      onClick={onClick}
    >
      <span style={iconStyle}>{icon}</span>
      <span style={textStyle}>{text}</span>
    </button>
  );
};

export default BackButton;
