import React, { useRef, useState } from "react";

interface GameBoardProps {
  board: string[][];
  selectedCells?: Array<[number, number]>;
  onCellSelect?: (row: number, col: number) => void;
  flashColor?: string | null; // 'green' | 'red' | null
}

// ...existing code...

const GameBoard: React.FC<GameBoardProps> = ({ board, selectedCells = [], onCellSelect, flashColor }) => {
  const getCoords = (idx: number) => [Math.floor(idx / 4), idx % 4] as [number, number];
  const [isDragging, setIsDragging] = useState(false);
  const dragCells = useRef<Array<[number, number]>>([]);

  // Начало свайпа
  const handleStart = (row: number, col: number) => {
    setIsDragging(true);
    dragCells.current = [[row, col]];
    if (onCellSelect) onCellSelect(row, col);
  };
  // Добавление ячейки при движении
  const handleEnter = (row: number, col: number) => {
    if (!isDragging) return;
    if (dragCells.current.some(([r, c]) => r === row && c === col)) return;
    dragCells.current.push([row, col]);
    if (onCellSelect) onCellSelect(row, col);
  };
  // Завершение свайпа
  const handleEnd = () => {
    setIsDragging(false);
    dragCells.current = [];
  };

  return (
    <div
      style={{ display: 'grid', gridTemplateColumns: 'repeat(4, 48px)', gap: 8 }}
      onMouseUp={handleEnd}
      onTouchEnd={handleEnd}
    >
      {board.flat().map((letter, idx) => {
        const [row, col] = getCoords(idx);
        const isSelected = selectedCells.some(([r, c]) => r === row && c === col);
        let bg = '#e0e0e0';
        if (isSelected) bg = '#90caf9';
        if (isSelected && flashColor === 'green') bg = '#a5d6a7';
        if (isSelected && flashColor === 'red') bg = '#ef9a9a';
        return (
          <div
            key={idx}
            style={{
              width: 48,
              height: 48,
              background: bg,
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              fontSize: 24,
              borderRadius: 8,
              cursor: onCellSelect ? 'pointer' : 'default',
              transition: 'background 0.2s',
              boxShadow: isSelected ? '0 0 0 2px #1976d2' : undefined
            }}
            onMouseDown={() => handleStart(row, col)}
            onMouseEnter={() => handleEnter(row, col)}
            onTouchStart={() => handleStart(row, col)}
            onTouchMove={e => {
              // Определяем координаты touch
              const touch = e.touches[0];
              const target = document.elementFromPoint(touch.clientX, touch.clientY);
              if (target && (target as HTMLElement).dataset && (target as HTMLElement).dataset.idx) {
                const idx2 = parseInt((target as HTMLElement).dataset.idx!);
                const [r2, c2] = getCoords(idx2);
                handleEnter(r2, c2);
              }
            }}
            data-idx={idx}
          >
            {letter}
          </div>
        );
      })}
    </div>
  );
};

export default GameBoard;
