import React, { useState, useEffect } from "react";
import type { Cell as CellType } from "./anagramGenerator";
import GameBoard from "./GameBoard";
import Timer from "./Timer";
// import WordInput from "./WordInput";
import Results from "./Results";
// Импортируем игровую логику
import { generateBoard, findAllWordsOnBoard, ROUND_DURATION, PAUSE_DURATION, Cell, letterBonuses } from "./anagramGenerator";
import { BonusType } from "./anagramGenerator";
import { isWordInDictionary, gameDictionary } from "./dictionary";
import uiStrings from './ui-strings-categorized.json';
import { canFormWordOnBoard } from "./anagramGenerator";



const getRandomWords = (count: number, dictionary: string[]): string[] => {
  const shuffled = [...dictionary].sort(() => Math.random() - 0.5);
  return shuffled.slice(0, count);
};

const App: React.FC = () => {
  const [screen, setScreen] = useState<'welcome' | 'loading' | 'game' | 'help'>('welcome');
  // Для свайп-старта
  const START_WORD = 'СТАРТ';
  // Слово СТАРТ в одну горизонтальную линию (1x5)
  const startBoard: CellType[][] = [
    [...START_WORD].map((letter, idx) => ({ letter, bonus: 'none', row: 0, col: idx }))
  ];
  const [startFlashColor, setStartFlashColor] = useState<string | null>(null);
  const [startSelected, setStartSelected] = useState<Array<[number, number]>>([]);
  const [loadingProgress, setLoadingProgress] = useState<number>(0);
  const [loadingMax, setLoadingMax] = useState<number>(0);
  const [loadingTimer, setLoadingTimer] = useState<number>(5);
  const [usedWords, setUsedWords] = useState<string[]>([]);
  // Параметры генерации
  const FIELD_SIZE = 4; // размер поля (4x4)
  const MIN_WORDS = 15; // минимальное количество слов (снизили с 20)
  const MAX_ATTEMPTS = 500; // увеличили количество попыток

  // Сохраняем поле, сгенерированное для анализа, чтобы использовать его для первого раунда
  const [initialBoard, setInitialBoard] = useState<Cell[][] | null>(null);
  const [board, setBoard] = useState<Cell[][]>(() => {
    // Новый генератор: ищем поле с максимальным количеством слов
    let bestBoard: Cell[][] | null = null;
    let bestWords: string[] = [];
    let maxWords = 0;
    
    // Создаём список частых букв из словаря для лучшей генерации
    const allLetters = gameDictionary.join('').toUpperCase().split('');
    const letterFreq: Record<string, number> = {};
    allLetters.forEach(letter => letterFreq[letter] = (letterFreq[letter] || 0) + 1);
    const frequentLetters = Object.keys(letterFreq).sort((a, b) => letterFreq[b] - letterFreq[a]);
    
    // Анализируем длинные слова для получения хороших комбинаций букв
    const longWords = gameDictionary.filter(word => word.length >= 5 && word.length <= 8);
    const longWordLetters: string[] = [];
    longWords.slice(0, 50).forEach(word => { // берём первые 50 длинных слов
      longWordLetters.push(...word.toUpperCase().split(''));
    });
    
    console.log("Генерируем поле, попыток:", MAX_ATTEMPTS);
    
    for (let attempts = 0; attempts < MAX_ATTEMPTS; attempts++) {
      // Генерируем поле с учётом частоты букв и длинных слов
      const letters: string[] = [];
      for (let i = 0; i < FIELD_SIZE * FIELD_SIZE; i++) {
        const rand = Math.random();
        if (rand < 0.3 && longWordLetters.length > 0) {
          // 30% - буквы из длинных слов
          const longIndex = Math.floor(Math.random() * longWordLetters.length);
          letters.push(longWordLetters[longIndex]);
        } else if (rand < 0.8) {
          // 50% - частые буквы
          const freqIndex = Math.floor(Math.random() * Math.min(10, frequentLetters.length));
          letters.push(frequentLetters[freqIndex]);
        } else {
          // 20% - случайные буквы
          const randIndex = Math.floor(Math.random() * frequentLetters.length);
          letters.push(frequentLetters[randIndex]);
        }
      }
      
      // Преобразуем в матрицу Cell[][] с бонусами
      const matrix: Cell[][] = [];
      for (let r = 0; r < FIELD_SIZE; r++) {
        matrix.push(letters.slice(r * FIELD_SIZE, (r + 1) * FIELD_SIZE).map((letter, c) => {
          let rawBonus = letterBonuses[letter.toUpperCase()];
          let bonus: BonusType = typeof rawBonus === 'string' ? rawBonus : 'none';
          return {
            letter,
            row: r,
            col: c,
            bonus
          };
        }));
      }
      // Находим все слова
      const possibleWords: string[] = findAllWordsOnBoard(matrix);
      if (possibleWords.length > maxWords) {
        bestBoard = matrix;
        bestWords = possibleWords;
        maxWords = possibleWords.length;
        console.log(`Попытка ${attempts}: найдено ${maxWords} слов`);
        // Если нашли поле с достаточным количеством слов — сразу используем
        if (maxWords >= MIN_WORDS) {
          console.log("Достигнуто минимальное количество слов, выбираем это поле");
          break;
        }
      }
    }
    
    console.log(`Финальное поле: ${maxWords} слов`);
  setUsedWords(bestWords.filter(word => word.length >= 3));
  setInitialBoard(bestBoard!);
    
    // Если не удалось — возвращаем случайное поле
    if (!bestBoard) {
      console.log("Не удалось найти поле с нужным количеством слов, генерируем случайное");
      const letters: string[] = [];
      for (let i = 0; i < FIELD_SIZE * FIELD_SIZE; i++) {
        const randIndex = Math.floor(Math.random() * gameDictionary.join('').length);
        letters.push(gameDictionary.join('')[randIndex]);
      }
      const matrix: Cell[][] = [];
      for (let r = 0; r < FIELD_SIZE; r++) {
        matrix.push(letters.slice(r * FIELD_SIZE, (r + 1) * FIELD_SIZE).map((letter, c) => {
          let rawBonus = letterBonuses[letter.toUpperCase()];
          let bonus: BonusType = typeof rawBonus === 'string' ? rawBonus : 'none';
          return {
            letter,
            row: r,
            col: c,
            bonus
          };
        }));
      }
      return matrix;
    }
    return bestBoard;
  });
  const [timeLeft, setTimeLeft] = useState<number>(ROUND_DURATION);
  const [foundWords, setFoundWords] = useState<string[]>([]);
  const [score, setScore] = useState<number>(0);
  const [message, setMessage] = useState<string>("");
  const [selectedCells, setSelectedCells] = useState<Array<[number, number]>>([]);
  const [currentWord, setCurrentWord] = useState<string>("");
  const [flashColor, setFlashColor] = useState<string | null>(null);
  const [phase, setPhase] = useState<'playing' | 'results' | 'analyzer'>('playing');
  const [possibleWords, setPossibleWords] = useState<string[]>([]);
  const leaderboard = [
    { name: "Вы", score }
  ];

  // useEffect для анимации загрузки (теперь строго после всех useState)
  useEffect(() => {
    if (screen === 'loading' && loadingProgress < loadingMax && loadingTimer > 0) {
      const step = Math.ceil(loadingMax / (loadingTimer * 20));
      const interval = setInterval(() => {
        setLoadingProgress(prev => Math.min(prev + step, loadingMax));
      }, 50);
      return () => clearInterval(interval);
    }
    // Когда загрузка закончилась, используем initialBoard для первого раунда
    if (screen === 'loading' && loadingTimer === 0 && initialBoard) {
      setBoard(initialBoard);
    }
  }, [screen, loadingProgress, loadingMax, loadingTimer, initialBoard]);

  useEffect(() => {
    if (screen === 'loading' && loadingTimer > 0) {
      const timer = setTimeout(() => setLoadingTimer(loadingTimer - 1), 1000);
      return () => clearTimeout(timer);
    } else if (screen === 'loading' && loadingTimer === 0) {
      setScreen('game');
    }
  }, [screen, loadingTimer]);

  // useEffect для анимации загрузки
  useEffect(() => {
    if (screen === 'loading' && loadingProgress < loadingMax && loadingTimer > 0) {
      const step = Math.ceil(loadingMax / (loadingTimer * 20));
      const interval = setInterval(() => {
        setLoadingProgress(prev => Math.min(prev + step, loadingMax));
      }, 50);
      return () => clearInterval(interval);
    }
  }, [screen, loadingProgress, loadingMax, loadingTimer]);

  useEffect(() => {
    if (screen === 'loading' && loadingTimer > 0) {
      const timer = setTimeout(() => setLoadingTimer(loadingTimer - 1), 1000);
      return () => clearTimeout(timer);
    } else if (screen === 'loading' && loadingTimer === 0) {
      setScreen('game');
    }
  }, [screen, loadingTimer]);

  // Таймер
  useEffect(() => {
    if (phase === 'playing' && timeLeft > 0) {
      const timer = setTimeout(() => setTimeLeft(timeLeft - 1), 1000);
      return () => clearTimeout(timer);
    }
    if (phase === 'playing' && timeLeft === 0) {
      setPossibleWords(usedWords.filter(word => word.length >= 3 && canFormWordOnBoard(word, board)));
      setSelectedCells([]);
      setCurrentWord("");
      setPhase('results');
      setTimeLeft(3); // 3 секунды на результаты (можно увеличить)
    }
    if (phase === 'results' && timeLeft > 0) {
      const timer = setTimeout(() => setTimeLeft(timeLeft - 1), 1000);
      return () => clearTimeout(timer);
    }
    if (phase === 'results' && timeLeft === 0) {
      setPhase('analyzer');
      setTimeLeft(PAUSE_DURATION); // 15 секунд анализатор
    }
    if (phase === 'analyzer' && timeLeft > 0) {
      const timer = setTimeout(() => setTimeLeft(timeLeft - 1), 1000);
      return () => clearTimeout(timer);
    }
    if (phase === 'analyzer' && timeLeft === 0) {
      // Новый раунд (и только для первого — используем initialBoard, потом обычная генерация)
      if (initialBoard) {
        const allPossibleWords = findAllWordsOnBoard(initialBoard).filter(word => word.length >= 3);
        setUsedWords(allPossibleWords);
        setBoard(initialBoard);
        setFoundWords([]);
        setPhase('playing');
        setTimeLeft(ROUND_DURATION);
        setInitialBoard(null); // чтобы дальше использовать обычную генерацию
      } else {
        const words = getRandomWords(6, gameDictionary);
        const board = generateBoard({ name: "", reward: "", words });
        const allPossibleWords = findAllWordsOnBoard(board).filter(word => word.length >= 3);
        setUsedWords(allPossibleWords);
        setBoard(board);
        setFoundWords([]);
        setPhase('playing');
        setTimeLeft(ROUND_DURATION);
      }
    }
  }, [timeLeft, phase, board]);



  // Обработка завершения пути (свайп/drag)
  const handlePathComplete = (path: Array<[number, number]>) => {
    if (path.length < 3) {
      setMessage(uiStrings.ошибки.минимумБукв);
      setFlashColor('red');
      setTimeout(() => setFlashColor(null), 300);
      setTimeout(() => { setMessage(""); setSelectedCells([]); setCurrentWord(""); }, 350);
      return;
    }
    const word = path.map(([r, c]) => board[r][c].letter).join("");
    const allWords = findAllWordsOnBoard(board).filter(word => word.length >= 3);
    if (foundWords.includes(word)) {
      setMessage(uiStrings.ошибки.ужеВводилось);
      setFlashColor('red');
      setTimeout(() => setFlashColor(null), 300);
      setTimeout(() => { setMessage(""); setSelectedCells([]); setCurrentWord(""); }, 350);
      return;
    }
    if (!allWords.includes(word) || !isWordInDictionary(word)) {
      setMessage(uiStrings.ошибки.нетСлова);
      setFlashColor('red');
      setTimeout(() => setFlashColor(null), 300);
      setTimeout(() => { setMessage(""); setSelectedCells([]); setCurrentWord(""); }, 350);
      return;
    }
    // Подсчёт очков по бонусам букв
    let wordScore = 0;
    path.forEach(([r, c]) => {
      const letter = board[r][c].letter.toUpperCase();
      wordScore += letterBonuses[letter] || 0;
    });
    setScore(score + wordScore);
    setFoundWords([...foundWords, word]);
    setMessage(`+${wordScore} очков`);
    setFlashColor('green');
    setTimeout(() => setFlashColor(null), 300);
    setTimeout(() => { setMessage(""); setSelectedCells([]); setCurrentWord(""); }, 350);
  };

  // Проверка слова при завершении выбора (например, тап по последней клетке или авто)
  useEffect(() => {
    // Проверяем только если длина слова >= 3
    if (currentWord.length >= 3) {
      // Проверяем, есть ли слово в возможных
      const allWords = findAllWordsOnBoard(board);
      if (allWords.includes(currentWord) && !foundWords.includes(currentWord)) {
        setFoundWords([...foundWords, currentWord]);
        setFlashColor('green');
        setTimeout(() => setFlashColor(null), 300);
        setTimeout(() => {
          setSelectedCells([]);
          setCurrentWord("");
        }, 350);
      } else if (currentWord.length > 3 && !allWords.includes(currentWord)) {
        setFlashColor('red');
        setTimeout(() => setFlashColor(null), 300);
        setTimeout(() => {
          setSelectedCells([]);
          setCurrentWord("");
        }, 350);
      }
    }
  }, [currentWord]);

  // Эмуляция смартфона: размеры Samsung Galaxy S24+ (CSS viewport 390x844px)
  const phoneFrameStyle: React.CSSProperties = {
    width: 390,
    height: 844,
    margin: '32px auto',
    borderRadius: 36,
    border: '8px solid #222',
    boxShadow: '0 0 32px #0006',
    background: '#111',
    overflow: 'hidden',
    position: 'relative',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'stretch',
    justifyContent: 'flex-start',
    minWidth: 390,
    minHeight: 844
  };
  const phoneContentStyle: React.CSSProperties = {
    width: '100%',
    height: '100%',
    background: '#fff',
    overflowY: 'auto',
    WebkitOverflowScrolling: 'touch',
    borderRadius: 28,
    position: 'relative',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'stretch',
    justifyContent: 'flex-start',
  };

  // Свайп-старт по слову СТАРТ
  const handleStartPath = (path: Array<[number, number]>) => {
    const word = path.map(([r, c]) => startBoard[r][c].letter).join("");
    if (word === START_WORD) {
      setStartFlashColor('green');
      setTimeout(() => {
        // Генерируем поле и считаем количество слов
        const words = getRandomWords(6, gameDictionary);
        const board = generateBoard({ name: "", reward: "", words });
        const allPossibleWords = findAllWordsOnBoard(board).filter(word => word.length >= 3);
        setLoadingMax(allPossibleWords.length);
        setLoadingProgress(0);
        setLoadingTimer(5);
        setScreen('loading');
        setStartSelected([]);
        setStartFlashColor(null);
      }, 350);
    } else {
      setStartFlashColor('red');
      setTimeout(() => {
        setStartSelected([]);
        setStartFlashColor(null);
      }, 350);
    }
  };

  // Обёртка для любого экрана внутри рамки смартфона
  const renderPhoneFrame = (content: React.ReactNode) => (
    <div style={{ background: '#222', minHeight: '100vh', minWidth: '100vw', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
      <div style={phoneFrameStyle}>
        <div style={phoneContentStyle}>
          {content}
        </div>
      </div>
    </div>
  );

  if (screen === 'welcome') {
    return renderPhoneFrame(
      <div style={{ fontFamily: 'sans-serif', padding: 24, width: '100%', maxWidth: 390, margin: '0 auto', textAlign: 'center' }}>
        <h1>Фундамент</h1>
        <div style={{ display: 'flex', justifyContent: 'center', margin: '32px 0 16px 0' }}>
          <div style={{ minWidth: 260 }}>
            <GameBoard
              board={startBoard}
              selectedCells={startSelected}
              onPathComplete={handleStartPath}
              onDragUpdate={setStartSelected}
              flashColor={startFlashColor}
              columnsCount={START_WORD.length}
            />
          </div>
        </div>
        <div style={{ fontSize: 16, color: '#888', marginBottom: 32 }}>Проведите по буквам, чтобы начать</div>
        <button style={{ margin: 12, padding: '12px 32px', fontSize: 18, cursor: 'pointer' }} onClick={() => setScreen('help')}>Справка</button>
      </div>
    );
  }

  if (screen === 'loading') {
    return renderPhoneFrame(
      <div style={{ fontFamily: 'sans-serif', padding: 24, width: '100%', maxWidth: 390, margin: '0 auto', textAlign: 'center', position: 'relative' }}>
        {/* Кнопка назад */}
        <button
          style={{ position: 'absolute', left: 8, top: 8, background: 'none', border: 'none', cursor: 'pointer', display: 'flex', flexDirection: 'column', alignItems: 'center' }}
          onClick={() => setScreen('welcome')}
        >
          <span style={{ fontSize: 28, lineHeight: 1, color: '#1976d2' }}>&larr;</span>
          <span style={{ fontSize: 13, color: '#1976d2', marginTop: 2 }}>Назад</span>
        </button>
        <h2>Загрузка поля...</h2>
        <div style={{ fontSize: 32, margin: '32px 0', color: '#1976d2', fontWeight: 700 }}>{loadingProgress}</div>
        <div style={{ fontSize: 18, marginBottom: 12 }}>Будет доступно слов: {loadingMax}</div>
        <div style={{ fontSize: 16, color: '#888' }}>Осталось секунд: {loadingTimer}</div>
      </div>
    );
  }
  if (screen === 'help') {
    return renderPhoneFrame(
      <div style={{ fontFamily: 'sans-serif', padding: 24, width: '100%', maxWidth: 390, margin: '0 auto', textAlign: 'center' }}>
        <h1>Фундамент</h1>
        <div style={{ fontSize: 20, margin: '32px 0' }}>Справка будет добавлена позже.</div>
        <button style={{ margin: 12, padding: '12px 32px', fontSize: 18, cursor: 'pointer' }} onClick={() => setScreen('welcome')}>Назад</button>
      </div>
    );
  }
  // Экран игры и анализатора
  if (phase === 'analyzer') {
    return renderPhoneFrame(
      <div style={{ fontFamily: 'sans-serif', padding: 24, width: '100%', maxWidth: 390, margin: '0 auto', textAlign: 'center', position: 'relative' }}>
        {/* Кнопка назад */}
        <button
          style={{ position: 'absolute', left: 8, top: 8, background: 'none', border: 'none', cursor: 'pointer', display: 'flex', flexDirection: 'column', alignItems: 'center' }}
          onClick={() => setScreen('welcome')}
        >
          <span style={{ fontSize: 28, lineHeight: 1, color: '#1976d2' }}>&larr;</span>
          <span style={{ fontSize: 13, color: '#1976d2', marginTop: 2 }}>Назад</span>
        </button>
        <h2>Анализатор поля</h2>
        <div style={{ fontSize: 18, margin: '16px 0' }}>Найдено слов: <b>{usedWords.length}</b></div>
        <div style={{ fontSize: 16, color: '#1976d2', marginBottom: 12 }}>До начала игры: {timeLeft} сек.</div>
        <div style={{ margin: '12px 0', padding: '8px', background: '#f5f5f5', borderRadius: 8, fontSize: 14, color: '#333', maxHeight: 120, overflowY: 'auto' }}>
          <b>Все слова на поле:</b>
          <div style={{ display: 'flex', flexWrap: 'wrap', gap: 8 }}>
            {usedWords.map(word => (
              <span key={word} style={{ background: '#e0e0e0', borderRadius: 4, padding: '2px 6px', margin: 2 }}>{word}</span>
            ))}
          </div>
        </div>
      </div>
    );
  }
  // Экран игры
  return renderPhoneFrame(
    <div style={{ fontFamily: 'sans-serif', padding: 24, width: '100%', maxWidth: 390, margin: '0 auto', position: 'relative' }}>
      {/* Кнопка назад */}
      <button
        style={{ position: 'absolute', left: 8, top: 8, background: 'none', border: 'none', cursor: 'pointer', display: 'flex', flexDirection: 'column', alignItems: 'center' }}
        onClick={() => setScreen('welcome')}
      >
        <span style={{ fontSize: 28, lineHeight: 1, color: '#1976d2' }}>&larr;</span>
        <span style={{ fontSize: 13, color: '#1976d2', marginTop: 2 }}>Назад</span>
      </button>
      <h1>Фундамент</h1>
      <div style={{ fontSize: 20, fontWeight: 600, marginBottom: 8 }}>
        {phase === 'playing' ? uiStrings.игра.заголовок : uiStrings.результаты.заголовок}
        {' '}
        <span style={{ fontSize: 18, fontWeight: 400, color: '#1976d2' }}>
          {timeLeft} сек.
        </span>
      </div>
      <div style={{ minHeight: 32, color: message.includes(uiStrings.прочее.очков) ? 'green' : 'red', fontWeight: 500 }}>{message}</div>
      <div style={{ marginBottom: 8 }}>{uiStrings.игра.очки}: {score}</div>
      {/* ОТЛАДКА: выводим все возможные слова на этом поле */}
      <div style={{ margin: '12px 0', padding: '8px', background: '#f5f5f5', borderRadius: 8, fontSize: 14, color: '#333', maxHeight: 120, overflowY: 'auto' }}>
        <b>Все слова на поле ({usedWords.length}):</b>
        <div style={{ display: 'flex', flexWrap: 'wrap', gap: 8 }}>
          {usedWords.map(word => (
            <span key={word} style={{ background: '#e0e0e0', borderRadius: 4, padding: '2px 6px', margin: 2 }}>{word}</span>
          ))}
        </div>
      </div>
      <GameBoard 
        board={board} 
        selectedCells={selectedCells}
        onPathComplete={phase === 'playing' ? handlePathComplete : undefined}
        onDragUpdate={phase === 'playing' ? setSelectedCells : undefined}
        flashColor={flashColor}
      />
      {phase === 'results' && (
        <Results 
          foundWords={foundWords} 
          possibleWords={possibleWords} 
          leaderboard={leaderboard}
          usedWords={usedWords}
        />
      )}
    </div>
  );
};

export default App;
