import React from "react";

interface GameBoardProps {
  board: string[][];
}

const GameBoard: React.FC<GameBoardProps> = ({ board }) => {
  return (
    <div style={{ display: 'grid', gridTemplateColumns: 'repeat(4, 48px)', gap: 8 }}>
      {board.flat().map((letter, idx) => (
        <div key={idx} style={{ width: 48, height: 48, background: '#e0e0e0', display: 'flex', alignItems: 'center', justifyContent: 'center', fontSize: 24, borderRadius: 8 }}>
          {letter}
        </div>
      ))}
    </div>
  );
};

export default GameBoard;
