import React from 'react';

interface BackButtonProps {
  onClick: () => void;
  text?: string;
  icon?: string;
  style?: React.CSSProperties;
}


export const BackButton: React.FC<BackButtonProps> = ({ 
  onClick, 
  text = "Назад", 
  style
}) => {
  // Стиль как у игровой плитки
  const tileStyle: React.CSSProperties = {
    position: 'absolute',
    left: 8,
    top: 8,
    width: 48,
    height: 48,
    background: '#e0e0e0',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    fontSize: 24,
    borderRadius: 8,
    cursor: 'pointer',
    transition: 'background 0.2s',
    fontWeight: 600,
    color: '#1976d2',
    border: 'none',
    boxShadow: '0 2px 8px rgba(25, 118, 210, 0.08)',
    zIndex: 2,
    ...style
  };

  return (
    <button
      style={tileStyle}
      onClick={onClick}
    >
      {text}
    </button>
  );
};

export default BackButton;
