import React from "react";
import uiStrings from './ui-strings-categorized.json';

interface ResultsProps {
  foundWords: string[];
  possibleWords: string[];
  leaderboard: { name: string; score: number }[];
}

const Results: React.FC<ResultsProps> = ({ foundWords, possibleWords, leaderboard }) => {
  return (
    <div style={{ marginTop: 24 }}>
      <h2>Результаты раунда</h2>
      <div>
        <strong>Найденные слова:</strong> {foundWords.join(", ") || "—"}
      </div>
      <div style={{ marginTop: 8 }}>
        <strong>{uiStrings.результаты.возможныеСлова}:</strong>
        <div style={{ marginTop: 4, padding: '8px 0', background: '#f5f5f5', borderRadius: 6, maxHeight: 120, overflowY: 'auto', fontSize: 15 }}>
          {possibleWords.length > 0
            ? possibleWords.map((word, idx) => (
                <span key={idx} style={{ marginRight: 8 }}>{word}</span>
              ))
            : '—'}
        </div>
      </div>
      <div style={{ marginTop: 16 }}>
        <strong>Лидерборд:</strong>
        <ol>
          {leaderboard.map((player, idx) => (
            <li key={idx}>{player.name}: {player.score}</li>
          ))}
        </ol>
      </div>
    </div>
  );
};

export default Results;
